mapper.select:
SELECT

FROM tasks

    scope = #{scope}  AND
    task_type = #{taskType}  AND
    task_status = #{taskStatus} 

ORDER BY gmt_create DESC
LIMIT #{pageSize}
OFFSET ${(pageIndex - 1) * pageSize}

SELECT
    COUNT(*) as cnt
FROM tasks

    scope = #{scope}  AND
    task_type = #{taskType}  AND
    task_status = #{taskStatus} 

ORDER BY gmt_create DESC
LIMIT #{pageSize}
OFFSET ${(pageIndex - 1) * pageSize}

mapper.update:
UPDATE tasks
SET task_status = #{taskStatus}
WHERE id = #{id}
